import NavInner from "./NavInner.jsx"
import "../customs/app.scss"
import MainBody from "./MainBody.jsx"
import { useState } from "react"
export default function App()  {
    const [opacity, setOpacity] = useState(1);
    const [active, setActive] = useState(false)

    function triggerO() {
        let o = (active) ? "0.2" : "0";
        setOpacity(o);
        console.log(opacity);
    }
    function triggerAtrue() {
        setActive(true);
        triggerO();
    }
    function triggerAfalse() {
        setActive(false);
        triggerO();
    }
    return (
        <div className = "highestContainer" style = {{opacity : opacity, transitionProperty : "opacity", transitionDuration: "0.8s"}}>
            <NavInner triggerTrue = {triggerAtrue} triggerFalse = {triggerAfalse}></NavInner>
            <MainBody></MainBody>
        </div>
            );
    }
