import NavInner from "./NavInner.jsx"
import "../customs/app.scss"
import MainBody from "./MainBody.jsx"
import { useState } from "react"
export default function App()  {
    const [opacity, setOpacity] = useState(1);
    const [active, setActive] = useState(false)

    function triggerO() {
        let o = (active) ? "0.2" : "1";
        setOpacity(o);
        console.log(opacity);
    }
    function triggerA(condition) {
        setActive(condition);
        triggerO();
    }
    return (
        <div className = "highestContainer" style = {{opacity : opacity, transitionProperty : "opacity", transitionDuration: "0.8s"}}>
            <NavInner triggerA = {triggerA}></NavInner>
            <MainBody></MainBody>
        </div>
            );
    }
